import Image from "next/image";
import cs from "classnames";
import type { NetworkName } from "../../../db/db";
interface ILogoProps {
  networkName?: NetworkName;
  size?: number;
  className?: string;
}

const ethereum =
  "data:image/svg+xml;base64,";
const polygon =
  "data:image/svg+xml;base64,";

const Images = {
  Sepolia: ethereum,
  Ethereum: ethereum,
  Polygon: polygon,
  "Polygon Mumbai": polygon,
};

export const Logo = (props: ILogoProps) => {
  const { networkName, size = 24, className } = props;

  if (!networkName) return null;

  const src = Images[networkName];

  return (
    <Image
      src={src}
      width={size}
      height={size}
      alt={networkName}
      className={cs(`w-[${size}px] h-[${size}px]`, className)}
    />
  );
};
